            "text": "/*\
title: $:/ak/plugins/EmbedYouTube/EmbedYouTube.js
type: application/javascript
module-type: macro


<<embedYouTube "YouTube url" "YouTube API">>

\*/

(function(){


"use strict";



exports.name = "embedYouTube";


exports.params = [{name: "url"}, {name: "APIKey"}];

/*
Run the macro
*/

exports.run = function(url, APIKey) {

 var url = url || "None";
 var encurl = encodeURI(url) || "None";
 var tiddlerTitle = "None";
 var tiddlerText = "None";


   var regExp = /.*(?:youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=)([^#\&\?]*).*/;
   var match = url.match(regExp);
//   console.log(">>>>>>>>>>>>>>>>>>>><<<<<<<<<<<<");
   var VideoCode = (match&&match[1].length==11)? match[1] : false ;
//   console.log(">>>>>>>>>>>>>>>>>>>><<<<<<<<<<<<");


//
var ytApiKey = APIKey;

//

   tiddlerText = "<iframe width='560' height='315' src='https://www.youtube.com/embed/" + VideoCode + "' frameborder='0' ></iframe>"

 //   console.log(">>>>>>>>>>>>>>>>>> " + tiddlerTitle + "<<<<<<<<<<<<<<<<<<");

// $tw.wiki.addTiddler({title:tiddlerTitle, text:tiddlerText});

// $.get(, function(data) {tiddlerTitle = (data.items[0].snippet.title);});

$tw.utils.httpRequest({url: "https://www.googleapis.com/youtube/v3/videos?part=snippet&id=" + VideoCode + "&key=" + ytApiKey,   callback: function (error,data){
    if (error){
    console.log("ERROR:"+error);
    }

    const parser = JSON.parse(data);

    //console.log(">>>>>>>>>>>>>>>>>> " + parser["items"][0]["snippet"]["title"] + "<<<<<<<<<<<<<<<<<<");

    tiddlerTitle =  parser["items"][0]["snippet"]["title"] ;

    $tw.wiki.addTiddler({title:tiddlerTitle, text:tiddlerText});

  }}) 


  return "[["+tiddlerTitle +"]]";
};


})();",
